import React, { useState, useEffect } from 'react';
import Slider from 'react-slick';

const CardSlider = () => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  const categories = [
    { title: "Trending Books", query: "Trending Books" },
    { title: "Classic Books", query: "Classic Books" },
    { title: "Romance", query: "Romance" },
    { title: "Kids", query: "Kids" },
    { title: "Thrillers", query: "Thrillers" }
  ];

  const [categoryData, setCategoryData] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const categoryPromises = categories.map(category => {
          return fetch(`https://openlibrary.org/search.json?q=${encodeURIComponent(category.query)}&limit=10`)
            .then(response => response.json());
        });

        const categoryResults = await Promise.all(categoryPromises);

        setCategoryData(categoryResults);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  return (
    <div>
      {categoryData.map((categoryResult, index) => (
        <div key={index}>
          <h2 className="text-xl font-bold mb-4">{categories[index].title}</h2>
          <Slider {...settings}>
            {categoryResult.docs.map((book, bookIndex) => (
              <div key={bookIndex}>
                <div className="border rounded-lg p-4">
                  <img src={`https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`} alt={book.title} className="w-full h-auto" />
                  <h3 className="text-lg font-semibold mt-2">{book.title}</h3>
                  <p className="text-gray-600">{book.author_name?.join(", ") || "Unknown Author"}</p>
                </div>
              </div>
            ))}
          </Slider>
        </div>
      ))}
    </div>
  );
};

export default CardSlider;
