import React, { useState } from 'react';
import { Card, CardHeader, CardBody, Typography, Avatar } from "@material-tailwind/react";

const BookCard = ({ book, addToBookshelf }) => {
  const coverUrl = book.cover_edition_key
    ? `https://covers.openlibrary.org/b/olid/${book.cover_edition_key}-M.jpg`
    : 'https://via.placeholder.com/150'; // Placeholder image if no cover is available

  return (
    <div className="p-4 border border-gray-300 rounded-md mb-4 flex">
      <img src={coverUrl} alt={book.title} className="w-24 h-36 mr-4" />
      <div>
        <h3 className="text-lg font-semibold">{book.title}</h3>
        <p className="text-gray-600">{book.author_name?.join(', ') || 'Unknown Author'}</p>
        <button
          onClick={() => addToBookshelf(book)}
          className="mt-2 px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
        >
          Add to Bookshelf
        </button>
      </div>
    </div>
  );
};

const SearchBooks = () => {
  const [query, setQuery] = useState('');
  const [loading, setLoading] = useState(false);
  const [searchResults, setSearchResults] = useState([]);

  const handleInputChange = (event) => {
    setQuery(event.target.value);
  };

  const handleSearch = () => {
    setLoading(true);
    // Perform search API call
    fetch(`https://openlibrary.org/search.json?q=${query}&limit=10&page=1`)
      .then(response => response.json())
      .then(data => {
        setSearchResults(data.docs);
        setLoading(false);
      })
      .catch(error => {
        console.error('Error fetching search results:', error);
        setLoading(false);
      });
  };

  return (
    <div>
      <input
        type="text"
        value={query}
        onChange={handleInputChange}
        placeholder="Search for a book..."
        className="w-full p-2 border border-gray-300 rounded-md mb-4"
      />
      <button
        onClick={handleSearch}
        className="px-4 py-2 bg-blue-500 text-white rounded-md hover:bg-blue-600"
      >
        Search
      </button>

      {loading && (
        <div className="loader-container flex justify-center items-center">
          <div className="loader"></div>
        </div>
      )}

      <div className="grid grid-cols-1 gap-6 md:grid-cols-3">
        {searchResults.map((book, index) => (
          <BookCard key={index} book={book} />
        ))}
      </div>
    </div>
  );
};

export default SearchBooks;
