import React, { useState, useEffect } from 'react';
import SearchBar from './SearchBar';
import SearchResults from './SearchResults';

const BookSearchPage = ({ addToBookshelf }) => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [searched, setSearched] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [currentQuery, setCurrentQuery] = useState('');

  useEffect(() => {
    // Add event listener when component mounts
    window.addEventListener('scroll', handleScroll);
    return () => {
      // Remove event listener when component unmounts
      window.removeEventListener('scroll', handleScroll);
    };
  }, []); // Empty dependency array ensures this effect runs only once on mount

  const handleScroll = () => {
    // Check if user has scrolled to the bottom of the page
    if (window.innerHeight + window.scrollY >= document.body.scrollHeight) {
      // Load more books if not currently loading and there are more books to load
      if (!loading && books.length > 0 && books.length % 10 === 0) {
        loadMoreBooks();
      }
    }
  };

  const searchBooks = async (query) => {
    if (query.length === 0) {
      setBooks([]);
      setSearched(false);
      return;
    }
    setLoading(true);
    setCurrentPage(1); // Reset to first page when performing a new search
    setCurrentQuery(query); // Store the current query
    const response = await fetch(`https://openlibrary.org/search.json?q=${query}&limit=10&page=1`);
    const data = await response.json();
    setBooks(data.docs);
    setLoading(false);
    setSearched(true);
  };

  const loadMoreBooks = async () => {
    setLoading(true);
    const nextPage = currentPage + 1;
    const response = await fetch(`https://openlibrary.org/search.json?q=${currentQuery}&limit=10&page=${nextPage}`);
    const data = await response.json();
    setBooks(prevBooks => [...prevBooks, ...data.docs]); // Append new books to the existing list
    setCurrentPage(nextPage);
    setLoading(false);
  };

  return (
    <div className="p-4">
      <SearchBar onSearch={searchBooks} />
      {loading ? (
        <div className="flex justify-center items-center mt-4">
          <div className="loader"></div>
        </div>
      ) : books.length > 0 ? (
        <div>
          <SearchResults books={books} addToBookshelf={addToBookshelf} />
          {searched && books.length % 10 === 0 && (
            <div className="flex justify-center mt-4">
              <div className="loader"></div>
            </div>
          )}
        </div>
      ) : searched ? (
        <div className="flex justify-center items-center mt-4 text-5xl">
          <p>No results found!!!</p>
        </div>
      ) : null}
    </div>
  );
};

export default BookSearchPage;
