import React, { useState } from 'react';
import { useLocation, Routes, Route, useNavigate } from 'react-router-dom';
import SearchBar from './SearchBar';
import SearchResults from './SearchResults';

const BookSearchPage = ({ addToBookshelf }) => {
  const navigate = useNavigate();
  const location = useLocation();
  const [books, setBooks] = useState([]);

  const searchBooks = async (query) => {
    // Your search logic here...
  };

  // Function to clear search results
  const clearSearchResults = () => {
    setBooks([]);
  };

  // Listen for route changes
  React.useEffect(() => {
    // If navigating to home, clear search results
    if (location.pathname === '/') {
      clearSearchResults();
    }
  }, [location.pathname]);

  return (
    <div>
      <SearchBar onSearch={searchBooks} />
      <Routes>
        <Route
          path="/"
          element={
            <Home
              // Pass the navigate function to the Home component
              navigate={navigate}
              clearSearchResults={clearSearchResults}
            />
          }
        />
        <Route path="/booksearch" element={<SearchResults books={books} addToBookshelf={addToBookshelf} />} />
      </Routes>
    </div>
  );
};

const Home = ({ navigate, clearSearchResults }) => {
  // Call clearSearchResults when navigating to home
  React.useEffect(() => {
    clearSearchResults();
  }, []);

  // Function to navigate to book search page
  const handleSearchClick = () => {
    navigate('/booksearch');
  };

  return (
    <div>
      <h1>Welcome to the Home Page!</h1>
      <p>This is where you can add content for your home page.</p>
      <button onClick={handleSearchClick}>Search Books</button>
    </div>
  );
};

export default BookSearchPage;
