import React, { useState } from 'react';
import { useNavigate, Routes, Route } from 'react-router-dom';
import SearchBar from './SearchBar';
import SearchResults from './SearchResults';

const BookSearchPage = ({ addToBookshelf }) => {
  const navigate = useNavigate();
  const [books, setBooks] = useState([]);
  const [isLoading, setIsLoading] = useState(false);
  const [clearResults, setClearResults] = useState(false);

  const searchBooks = async (query) => {
    setIsLoading(true);
    if (query.length === 0) {
      setBooks([]);
      setIsLoading(false);
      return;
    }
    const response = await fetch(`https://openlibrary.org/search.json?q=${query}&limit=10&page=1`);
    const data = await response.json();
    setBooks(data.docs);
    setIsLoading(false);
    // Navigate to /search after search is performed
    navigate('/search');
  };

  const clearSearchResults = () => {
    setBooks([]);
    setClearResults(true); // Set clearResults to true when home button is clicked
  };

  return (
    <div>
      <SearchBar onSearch={searchBooks} />
      {/* Show loader while loading */}
      {isLoading && <div className="loader"></div>}
      {/* Only render SearchResults if clearResults is false */}
      {!clearResults && <SearchResults books={books} addToBookshelf={addToBookshelf} />}
      <Routes>
        {/* Route to clear search results when navigating back to home */}
        <Route path="/" element={<Home clearSearchResults={clearSearchResults} />} />
      </Routes>
    </div>
  );
};

const Home = ({ clearSearchResults }) => {
  // Call clearSearchResults when component mounts
  clearSearchResults();
  return null; // Render nothing
};

export default BookSearchPage;
