import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Link, Routes } from 'react-router-dom';
import BookSearchPage from './components/BookSearchPage';
import BookshelfPage from './components/BookshelfPage';
import Home from './components/Home';
import { Toaster } from 'react-hot-toast';

const App = () => {
  const [searchResults, setSearchResults] = useState([]); // State to hold search results

  // Function to clear search results
  const clearSearchResults = () => {
    setSearchResults([]);
  };

  return (
    <Router>
      <div className="p-6">
        <Toaster />
        <nav className="flex mb-4 justify-evenly">
          {/* Link to the Home component */}
          <Link to="/" onClick={clearSearchResults} className="text-white  right-2.5 bottom-2.5 bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">Home</Link>
          <h1 className='text-3xl'>Personal BookShelf</h1>
          {/* Link to the BookshelfPage component */}
          <Link to="/bookshelf" className="text-white right-2.5 bottom-2.5 bg-blue-700 hover:bg-blue-800 focus:ring-4 focus:outline-none focus:ring-blue-300 font-medium rounded-lg text-sm px-4 py-2 dark:bg-blue-600 dark:hover:bg-blue-700 dark:focus:ring-blue-800">My Bookshelf</Link>
        </nav>
        <Routes>
          {/* Route to the Home component */}
          <Route path="/" element={<Home />} />
          {/* Route to the BookSearchPage component */}
          <Route path="/booksearch" element={<BookSearchPage />} />
          {/* Route to the BookshelfPage component */}
          <Route path="/bookshelf" element={<BookshelfPage />} />
        </Routes>
      </div>
    </Router>
  );
};

export default App;
