import React, { useEffect, useState } from 'react';
import Slider from 'react-slick';

const CardSlider = ({ category }) => {
  const settings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
  };

  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(`https://openlibrary.org/search.json?q=${encodeURIComponent(category)}&limit=10`);
        const data = await response.json();
        setBooks(data.docs);
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, [category]);

  return (
    <div>
      <h2 className="text-xl font-bold mb-4">{category}</h2>
      <Slider {...settings}>
        {books.map((book, index) => (
          <div key={index}>
            <div className="border rounded-lg p-4">
              <img src={`https://covers.openlibrary.org/b/id/${book.cover_i}-M.jpg`} alt={book.title} className="w-full h-auto" />
              <h3 className="text-lg font-semibold mt-2">{book.title}</h3>
              <p className="text-gray-600">{book.author_name?.join(", ") || "Unknown Author"}</p>
            </div>
          </div>
        ))}
      </Slider>
    </div>
  );
};

export default CardSlider;
